import React, { useState, useContext } from "react";
import { TextInput, Button, Alert, Text, SafeAreaView, ImageBackground, StyleSheet } from 'react-native';
import { createStackNavigator } from "@react-navigation/stack";
import { buildPath } from './functions/BuildPath';
import { Center } from './components/Center';
import { AuthContext } from './AuthProvider';
import { Card } from 'react-native-paper';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Stack = createStackNavigator();

export const AuthStack = ({}) => {
  return (
    <Stack.Navigator initialRouteName="Login">
      <Stack.Screen
        options={{
          headerTitle: "Sign In"
        }}
        name="Login"
        component={Login}
      />
      <Stack.Screen
        options={{
          headerTitle: "Sign Up"
        }}
        name="Register"
        component={Register}
      />
    </Stack.Navigator>
  );
};

function Login({navigation}) {
  const {login} = useContext(AuthContext);
  const [loginEmail, setEmail] = React.useState('');
  const [loginPassword, setPassword] = React.useState('');
  //var loginEmail;
  //var loginPassword;

  var message = '';

  const [ showAlert, setAlert ] = useState(false);

  const doLogin = async event => {
      event.preventDefault();

      var obj = {email:loginEmail.value, password:loginPassword.value};
      var js = JSON.stringify(obj);
      try
      {
          // API call
          const response = await fetch(buildPath('api/login'),
              {method:'POST',body:js,headers:{'Content-Type': 'application/json'}});

          // Parse JSON response
          var res = JSON.parse(await response.text());

          if( res.error != '')
          {
            message = 'Email/Password combination incorrect';
            setAlert(true);
          }
          else
          {
            login(res.id, res.accessToken);
          }
      }
      catch(e)
      {
          Alert.alert(e.toString);
          return;
      }
  }

  return (
      <ImageBackground 
      style={styles.backgroundImage} 
      source={require('../assets/bubblebackground.png')}>
        <SafeAreaView style={styles.loginBox}>
          <SafeAreaView style={{left: 45}}>
            <Button style={styles.submitButton}
              title="Register"
              color="#FB2B60"
              onPress={() => {
                navigation.navigate("Register");
              }}/>
          </SafeAreaView>
          <Center>
            <TextInput style={styles.input}
              onChangeText={em => setEmail(em)}
              placeholder="Email"
              value={loginEmail}>
            </TextInput>
            <TextInput style={styles.input}
              onChangeText={pw => setPassword(pw)}
              placeholder="Password"
              value={loginPassword}>
            </TextInput>
          </Center>
        </SafeAreaView>
        <SafeAreaView>
          <Button style={styles.submitButton}
            color="#FB2B60"
            title="Login"
            onPress={() => {doLogin}}/>
          <Text style={styles.medium}>Forgot Password? Click here.</Text>
        </SafeAreaView>
      </ImageBackground>
  );
};

function Register({ navigation }) {
  // Register usestates
  const [username, setUsername] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [email, setEmail] = React.useState('');

  return (
    <ImageBackground 
    style={styles.backgroundImage} 
    source={require('../assets/bubblebackground.png')}>
      <SafeAreaView style={styles.container}>
        <Center>
          <Text style={styles.large}>Register Form</Text>
          <TextInput style={styles.input}
            onChangeText={u => setUsername(u)}
            placeholder="Username"
            value={username}></TextInput>
          <TextInput style={styles.input}
            onChangeText={em => setEmail(em)}
            placeholder="Email"
            value={email}></TextInput>
          <TextInput style={styles.input}
            onChangeText={pw => setPassword(pw)}
            placeholder="Password"
            value={password}></TextInput>
          <Button style={styles.submitButton}
            title="Log in"
            onPress={doLogin}/>
        </Center>
      </SafeAreaView>
    </ImageBackground>  
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "space-between",
    padding: 20,
    margin: 10,
  },
  input: {
    margin: 1,
    height: 40,
    width: 150,
    backgroundColor: 'white',
    borderColor: 'white',
    borderWidth: 1,
  },
  submitButton: {
    backgroundColor: '#7a42f4',
    padding: 10,
    margin: 25,
    height: 40,
  },
  submitButtonText: {
    color: 'white'
  },
  large: {
    fontSize: 28
  },
  medium: {
    fontSize: 20
  },
  backgroundImage: {
    flex: 1,
    resizeMode: 'cover',
  }, 
  loginBox: {
    flex: 0.3,
    backgroundColor: "#19c0ff",
    width: 250,
    borderWidth: 2,
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
    borderBottomLeftRadius: 20,
    borderBottomRightRadius: 20,
    margin: 10,
    alignItems: 'center',
    paddingTop: 10
},
});
