import React, { useContext } from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'
import { Center } from './Center';
import { Text, Button } from 'react-native';
import { AuthContext } from './AuthProvider';
import Icon from "react-native-vector-icons/FontAwesome";
//import Icon from 'react-native-vector-icons/FontAwesome';
//import { createNavigationContainer } from 'react-navigation';

const Tabs = createBottomTabNavigator();

function Home() {
    const {logout} = useContext(AuthContext);
    return (
        <Center>
            <Text>Home</Text>
            <Button 
                title='logout'
                onPress={() => logout()}
            />
        </Center>
    );
}

function Budgets() {
    return (
        <Center>
            <Text>Budgets</Text>
        </Center>
    );
}

function Leaderboard() {
    return (
        <Center>
            <Text>Leaderboard</Text>
        </Center>
    );
}

function Settings() {
    return (
        <Center>
            <Text>Settings</Text>
        </Center>
    );
}

export const AppTabs = ({}) => {
    return (
        <Tabs.Navigator
            screenOptions={({ route }) => ({
                tabBarIcon: ({ focused, color, size }) => {
                    let iconName;

                    if (route.name === "Home") {
                        iconName = "home";
                        return <Icon name="home" size={25} color={tintColor} />
                    }
                    else if (route.name === "Budgets") {
                        iconName = "budgets";
                        return <Icon name="line-chart" size={25} color={tintColor} />
                    }
                    else if (route.name === "Leaderboard") {
                        iconName = "leaderboard";
                        return <Icon name="users" size={25} color={tintColor} />
                    }
                    else if (route.name === "Settings") {
                        iconName = "settings";
                        return <Icon name="cogs" size={25} color={tintColor} />
                    }
                }
            })}
            tabBarOptions={{
                activeTintColor: "blue",
                inactiveTintColor: "gray"
            }}
        >
            <Tabs.Screen name='Home' component={Home} />
            <Tabs.Screen name='Budgets' component={Budgets} />
            <Tabs.Screen name='Leaderboard' component={Leaderboard} />
            <Tabs.Screen name='Settings' component={Settings} />
        </Tabs.Navigator>
    );
};
